    }

    return (
        <Fragment>
            <HighlightedQuote text={quote.text} auhtor={quote.author}/>
            <Route path={`/quotes/${params.quoteId}/comments`}>
                <Comments />
            </Route>
        </Fragment>
    );
};

export default QuoteDetail;
